import { faMinus, faPlus } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import styled from "styled-components"
import Announcment from "../components/Announcment"
import Footer from "../components/Footer"
import Navbar from "../components/navbar"
import Newsletter from "../components/Newsletter"

const Container = styled.div`

`

const Wrapper = styled.div`
    padding: 50px;
    display: flex;
`

const ImgContainer = styled.div`
    flex: 1;
`

const Image = styled.img`
    height: 35vh;
    width: 100%;
    object-fit: cover;
`

const InfoContainer = styled.div`
    flex: 1;
    padding: 0px 50px;
`

const Title = styled.h1`
    font-weight: 200;
`

const Desc = styled.p`
    margin: 20px 0px;
`

const Price = styled.span`
    font-weight: 100;
    font-size: 30px;
`

const AddContainer = styled.div`
    width:  50%;
    display: flex;
    align-item: center;
    justify-content: space-between;
`

const AmountContainer = styled.div`
    display: flex;
    align-item: center;
    font-weight: 700;
`

const Amount = styled.span`
    width:30px;
    height: 30px;
    border-radius: 10px;
    border: 1px solid pink;
    display: flex;
    align-item: center;
    justify-content: center;
`

const Button = styled.button`

`

export default function ProductPage() {
  return (
    <Container>
        <Announcment/>
        <Navbar/>
        <Wrapper>
            <ImgContainer>
                <Image src= "https://cdn.shopify.com/s/files/1/3099/8088/products/bento_1512x.jpg?v=1649695087" />
            </ImgContainer>
            <InfoContainer>
                <Title>GMK BENTO R2</Title>
                <Desc>Lorem ipsum dolor sit amet consectetur adipisicing elit. Iure voluptatem doloribus consectetur tempore sint expedita eveniet! Eos asperiores quisquam, dolore voluptate sit accusantium nostrum adipisci voluptates architecto odit pariatur deleniti!</Desc>
                <Price>$ 500</Price>

            {/* <FilterContainer>
                <Filter>
                    <FilterTitle>
                        Color
                    </FilterTitle>
                </Filter>
            </FilterContainer> */}
            <AddContainer>
            <AmountContainer>
                <FontAwesomeIcon icon={faMinus}/>
                <Amount>1</Amount>
                <FontAwesomeIcon icon={faPlus}/>
            </AmountContainer>
            <Button>ADD TO CART</Button>
            </AddContainer>
            </InfoContainer>
    </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}
