import { faCircleArrowLeft, faCircleArrowRight } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import styled from "styled-components"

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;

    position: relative;
    overflow: hidden;
`

const Arrow = styled.div`
  width:50px;
  height: 50px;
  font-size: 30px;
  color: white;
  background-color: black;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"};
  right: ${props=> props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
`

const Wrapper = styled.div`
  height: 100%;
  display: flex;
`

const Slide = styled.div`
  display: flex;
  align-items: center;
  width: 100vw;
  height: 100vh;
  background-color #${props=>props.bg}
`

const ImgContainer = styled.div`
  flex: 1;
  height: 100%;
`
const Image = styled.img`
  width: 100%;
`

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`

const Title = styled.h1`
font-size: 70px;
`
const Desc = styled.p`
margin: 50px 0px;
font-size: 20px;
font-weight: 500;
letter-spacing: 3px
`
const Button = styled.button`
padding:10px;
font-size: 20px;
background-color: transparent;
cursor: pointer;
`

export default function Slider() {
  return (
    <Container> 
        <Arrow direction="left" onClick={handleClick}>
           <FontAwesomeIcon icon={faCircleArrowLeft}/> 
        </Arrow>
          <Wrapper>
            <Slide bg="fcf1ed">


            <ImgContainer>
              <Image src='https://cdn.shopify.com/s/files/1/3099/8088/products/bento_3000x.jpg?v=1649695087'/>
            </ImgContainer>
            <InfoContainer>
              <Title>
                GMK BENTO R2
              </Title>
              <Desc>
                GMK Bento (inspired by the Japanese culture, and the iconic home packed meal called Bento) Returns!
              </Desc>
              <Button>
                Pre-Order Now
              </Button>
            </InfoContainer>
            </Slide>
            <Slide bg="fcf1ed">


            <ImgContainer>
              <Image src='https://cdn.shopify.com/s/files/1/3099/8088/products/Notion_Inset_3000x.jpg?v=1630328280'/>
            </ImgContainer>
            <InfoContainer>
              <Title>
                NOTION DESKPADS
              </Title>
              <Desc>
                These deskpads were designed to compliment notion.
              </Desc>
              <Button>
                Order Now
              </Button>
            </InfoContainer>
            </Slide>
          </Wrapper>

        <Arrow direction="right">
           <FontAwesomeIcon icon={faCircleArrowRight}/> 
        </Arrow>
    </Container>
  )
}
